create table categoria (id bigint generated by default as identity, descricao varchar(255), nome varchar(255), primary key (id));
create table fornecedor (id bigint generated by default as identity, email varchar(255), nif varchar(255), nome varchar(255), telefone varchar(255), primary key (id));
create table produto (preco float(53) not null, quantidade integer not null, categoria_id bigint, fornecedor_id bigint, id bigint generated by default as identity, nome varchar(255), primary key (id));
alter table if exists produto add constraint FKopu9jggwnamfv0c8k2ri3kx0a foreign key (categoria_id) references categoria;
alter table if exists produto add constraint FKo6c1dbi17sempey5dpnx6ovrj foreign key (fornecedor_id) references fornecedor;
